export function getRestakingErrorMessage(code: RestakingError): string {
  if (process.env.NODE_ENV !== 'production') {
    return (restakingErrorMessages as Record<RestakingError, string>)[code];
  }

  return 'Error message not available in production bundles.';
}

export function isRestakingError<TProgramErrorCode extends RestakingError>(
  error: unknown,
  transactionMessage: {
    instructions: Record<number, { programAddress: Address }>;
  },
  code?: TProgramErrorCode
): error is SolanaError<typeof SOLANA_ERROR__INSTRUCTION_ERROR__CUSTOM> &
  Readonly<{ context: Readonly<{ code: TProgramErrorCode }> }> {
  return isProgramError<TProgramErrorCode>(
    error,
    transactionMessage,
    RESTAKING_PROGRAM_ADDRESS,
    code
  );
}
